import React from 'react';

class SearchBar extends React.Component {

state={
  searchQuery: ""
}

  render() {
    return (
      <div>
        <br></br>
        <div class="input-group mb-3">
          <input type="text" onChange={(event) =>this.setState({searchQuery : event.target.value})} class="form-control" placeholder="Search a movie.." aria-label="Recipient's username" aria-describedby="basic-addon2" />
          <div className="input-group-append">
            <button class="btn btn-outline-primary" type="button">Search</button>
          </div>
        </div>
        
      </div>
    )
  }
}

export default SearchBar;
