import React from 'react';

class SearchBar extends React.Component {

  handleFormSubmit = (event) => {
    event.preventDefault();
  }

  render() {
    return (
      <form onSubmit={this.handleFormSubmit}>
        <br></br>
        <div className="input-group mb-3 col-10">
          <input type="text"  onChange={this.props.searchMovieProp} className="form-control" placeholder="Search a movie.." />
        </div>
        <div className="col-2">
          <button type="button" className="btn btn-md btn-danger" style={{float:"right"}}>Add Movie</button>
        </div>
      </form>
    )
  }
}

export default SearchBar;
